INSERT INTO `blade_visual_component` VALUES (1549433589344362498, '搜索组件', '<template>\n  <el-form :inline=\"true\" :model=\"form\">\n    <el-form-item label=\"审批人\">\n      <el-input v-model=\"form.user\" placeholder=\"请输入审批人\"></el-input>\n    </el-form-item>\n    <el-form-item label=\"活动区域\">\n      <el-select v-model=\"form.region\" placeholder=\"请输入活动区域\">\n        <el-option label=\"区域一\" value=\"shanghai\"></el-option>\n        <el-option label=\"区域二\" value=\"beijing\"></el-option>\n      </el-select>\n    </el-form-item>\n    <el-form-item>\n      <el-button type=\"primary\" @click=\"onSubmit\">查询</el-button>\n    </el-form-item>\n  </el-form>\n</template>\n<script>\n  export default {\n    data() {\n      return {\n        form: {\n          user: \'\',\n          region: \'\'\n        }\n      }\n    },\n    methods: {\n      onSubmit() {\n        this.$emit(\'click\',this.form)\n      }\n    }\n  }\n</script>', NULL, NULL, NULL);
INSERT INTO `blade_visual_component` VALUES (1549434249263570945, 'layer弹窗组件', '<template>\n    <div class=\"test\">\n        <el-button type=\"success\" @click=\"handleClick\">点击我弹窗</el-button>\n    </div>\n</template>\n<script>\nexport default{\n    data(){\n        return{\n            \n        }\n    },\n    created(){\n        \n    },\n    methods:{\n        handleClick(){\n            window.$loadScript(\'js\',\'https://cdn.staticfile.org/jquery/2.1.2/jquery.js\').then(()=>{\n                return window.$loadScript(\'js\',\'https://data.avuejs.com/layer/layer.js\')\n            }).then(()=>{\n                return window.$loadScript(\'js\',\'https://cdn.staticfile.org/jquery.qrcode/1.0/jquery.qrcode.min.js\')\n            }).then(()=>{\n                layer.open({\n                    title: \'生成二维码\',\n                    content: \'<div id=\"qrcode\"></div>\',\n                    success: function(layero, index){\n                        var config={\n                            width:200,\n                            height:200,\n                            text:\"苦逼的程序员\"\n                        }\n                        $(\"#qrcode\").qrcode(config);\n                    }\n                });\n                \n            })\n        }\n    }\n}\n</script>\n<style>\n    .test{\n        text-align:center;\n        color:red;\n        font-size:40px;\n    }\n</style>', NULL, NULL, NULL);
INSERT INTO `blade_visual_component` VALUES (1549441329026424834, 'table表格', '<template>\n    <el-table\n      :data=\"tableData\"\n      style=\"width: 100%\">\n      <el-table-column\n        prop=\"date\"\n        label=\"日期\"\n        width=\"180\">\n      </el-table-column>\n      <el-table-column\n        prop=\"name\"\n        label=\"姓名\"\n        width=\"180\">\n      </el-table-column>\n      <el-table-column\n        prop=\"address\"\n        label=\"地址\">\n      </el-table-column>\n    </el-table>\n  </template>\n\n  <script>\n    export default {\n      data() {\n        return {\n          tableData: [{\n            date: \'2016-05-02\',\n            name: \'王小虎\',\n            address: \'上海市普陀区金沙江路 1518 弄\'\n          }, {\n            date: \'2016-05-04\',\n            name: \'王小虎\',\n            address: \'上海市普陀区金沙江路 1517 弄\'\n          }, {\n            date: \'2016-05-01\',\n            name: \'王小虎\',\n            address: \'上海市普陀区金沙江路 1519 弄\'\n          }, {\n            date: \'2016-05-03\',\n            name: \'王小虎\',\n            address: \'上海市普陀区金沙江路 1516 弄\'\n          }]\n        }\n      }\n    }\n  </script>', NULL, NULL, NULL);

